jQuery(document).ready(function(){CITYGOV_STORAGE.media_frame=null;CITYGOV_STORAGE.media_link="";jQuery(".citygov_media_selector").on("click",function(a){citygov_show_media_manager(this);a.preventDefault();return false})});function citygov_show_media_manager(a){CITYGOV_STORAGE.media_link=jQuery(a);if(CITYGOV_STORAGE.media_frame){CITYGOV_STORAGE.media_frame.open();return false}CITYGOV_STORAGE.media_frame=wp.media({title:CITYGOV_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:CITYGOV_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:CITYGOV_STORAGE.media_link.data("update"),close:true}});CITYGOV_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+CITYGOV_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(CITYGOV_STORAGE.media_link.data("multiple")===true){CITYGOV_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=CITYGOV_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});CITYGOV_STORAGE.media_frame.open();return false};